INSERT INTO CINEPHILES.MOVIES (M_ID,TITLE,RELEASE_DATE,DURATION,SYNOPSIS,POSTER_URL,AD_ID,D_ID,BACK_POSTER_URL) VALUES (1018494,'Operation Seawolf',TIMESTAMP'2022-10-07 00:00:00.0',120,'During the last days of World War II, Germany, desperate for any last grasp to defeat the allied powers, looked to their last remaining weapons and soldiers. The German Navy and the last remaining U-Boats were formed together for one desperate last mission – a mission to attack the United States Homeland, known as Operation Seawolf. Captain Hans Kessler, a grizzled submarine commander from both World Wars, is called into service to make one mission a success and help turn the tide of the war.','https://image.tmdb.org/t/p/original/eqm5EAyC9hJCN5qutuW4Ka1xYbU.jpg',1,1,'https://image.tmdb.org/t/p/original/6fYTLy4QE55BgMiHF3Co7bErjry.jpg');
